sqlMap.select:
SELECT item.ADD_TIME ITEM_ADD_TIME, item.FIRST_ADD_TIME ITEM_FIRST_ADD_TIME, GROUP_CONCAT(item.TAGS) BOOK_TAGS, info.ID BOOK_INFO_ID, info.AUTHOR BOOK_AUTHOR, info.NAME BOOK_NAME, info.PUBLISHER BOOK_PUBLISHER, info.PUBLISH_TIME BOOK_PUBLISH_TIME, info.ISBN BOOK_ISBN, info.CATEGORY_ID BOOK_CATEGORY_ID, info.IMG_URL BOOK_IMG_URL, info.DESCRIPTION BOOK_DESCRIPTION, info.EDITION BOOK_EDITION, GROUP_CONCAT(m.LOGIN_ID) BOOK_OWNERS, info.PAGES BOOK_PAGES, info.TRANSLATOR BOOK_TRANSLATOR, info.E_BOOK_URL E_BOOK_URL FROM BOOK_INFO info LEFT JOIN (BOOK_ITEM item ,MEMBER m) ON (
item.BOOK_INFO_ID = info.ID AND item.LOGIN_ID = m.LOGIN_ID AND item.STATE<>'deleted'
) GROUP BY info.ID ORDER BY info.ID DESC LIMIT #skipRow#,#pageSize#

SELECT item.ADD_TIME ITEM_ADD_TIME, item.FIRST_ADD_TIME ITEM_FIRST_ADD_TIME, GROUP_CONCAT(item.TAGS) BOOK_TAGS, info.ID BOOK_INFO_ID, info.AUTHOR BOOK_AUTHOR, info.NAME BOOK_NAME, info.PUBLISHER BOOK_PUBLISHER, info.PUBLISH_TIME BOOK_PUBLISH_TIME, info.ISBN BOOK_ISBN, info.CATEGORY_ID BOOK_CATEGORY_ID, info.IMG_URL BOOK_IMG_URL, info.DESCRIPTION BOOK_DESCRIPTION, info.EDITION BOOK_EDITION, GROUP_CONCAT(m.LOGIN_ID) BOOK_OWNERS, info.PAGES BOOK_PAGES, info.TRANSLATOR BOOK_TRANSLATOR, info.E_BOOK_URL E_BOOK_URL FROM BOOK_INFO info LEFT JOIN (BOOK_ITEM item ,MEMBER m) ON (
item.BOOK_INFO_ID = info.ID AND item.LOGIN_ID = m.LOGIN_ID AND item.STATE<>'deleted' AND ( (item.GMT_MODIFIED >= #startTime:DATETIME# AND item.GMT_MODIFIED < #endTime:DATETIME#) OR (info.GMT_MODIFIED >= #startTime:DATETIME# AND info.GMT_MODIFIED < #endTime:DATETIME#) )
) GROUP BY info.ID ORDER BY info.ID DESC
